import 'package:flutter/material.dart';

class Assignment3 extends StatefulWidget {
  const Assignment3({super.key});

  @override
  State<Assignment3> createState() => _Assignment3State();
}

class _Assignment3State extends State<Assignment3> {
  int? selectedIndex = 0;
  List<String> imageList = [
    'data:image/jpeg;base64,',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSI4Z86lgrjLDhEmTeLbuoXVnsB0X9kws_QS2_aHel_Whil9xFQNTzZnUKCIgVF1BnX5dU&usqp=CAU',
    'data:image/jpeg;base64,',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQygVIfabSU4KeQJRIX2y8k-7KAkD41TDEd2jQMLV2uxh1zEI7gQDTEUcnTBhrOjSKdRQk&usqp=CAU',
  ];

  void showNextImage() {
    setState(() {
      selectedIndex = selectedIndex! + 1;
      if (selectedIndex == imageList.length) {
        selectedIndex = 0;
      }
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text('Display Image')),
      body: Center(
          child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Image.network(
            imageList[selectedIndex!],
            height: 300,
            width: 300,
          ),
          const SizedBox(
            height: 20,
          ),
          ElevatedButton(
            onPressed: showNextImage,
            child: Text('Next'),
          ),
          const SizedBox(
            height: 20,
          ),
          ElevatedButton(
            onPressed: () => setState(() {
              selectedIndex = 0;
            }),
            child: Text('reset'),
          ),
        ],
      )),
    );
  }
}
